UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>- <strong>כל הבאים אליה לא ישובון</strong> מדרכם הרע, כי קשה מאד לפרוש ממנה; <strong>ולא ישיגו</strong> עוד מעתה <strong>ארחות חיים</strong>.</p>\r\n<p>ונאמר אף למשל על דרכי המינות, כי קשה מאוד לפרוש ממנה אחר שהורגל בה.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 19 AND 1 LIMIT 1;

