UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>איש <strong>טוב</strong> מוציא <strong>רצון מה\',</strong> להביא טובה על כל העולם.</p><p><strong>ואיש מזמות</strong> - הנמשך אחר מחשבות לבו הוא <strong>ירשיע</strong> את כל העולם, כי מכריעם לחובה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 2 AND 1 LIMIT 1;

