UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>אויל</strong> בו <strong>ביום</strong> שכועס על מי, ביום הזה <strong>יודע כעסו</strong> ויתפרסם בו בעולם, כי מיד יתקוטט עמו ויבזהו;&#160;&#160; אבל ה<strong>ערום</strong>, עם כי יכעס, <strong>כוסה</strong> הוא את ה<strong>קלון</strong>, ולא יבזה את רעהו מיד בפרסום.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 16 AND 1 LIMIT 1;

