UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יש בוטה</strong> - <strong>יש</strong> מי <strong>יבטא</strong> בשפתיו ויזיק בזה כאלו <strong>מדקיר בחרב</strong>, והוא המדבר לשון הרע ומוציא שם רע;&#160;&#160;&#160; אבל אמרי <strong>לשון חכמים</strong> המה עוד ל<strong>מרפא</strong>, כי במתק אמרי נועם יסית האדם לתשובה ושב ורפא לו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 18 AND 1 LIMIT 1;

