SET character_set_connection=utf8;
UPDATE `sgulot` SET `mcudot` = '\r\n\r\n<p><strong>יתן חכמה</strong> - <strong>יתן</strong> לך <strong>חכמה מפיו</strong>, ר\"ל: יגלה לך מסתרי החכמה, כאילו מפיו יאמר לך.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 6 AND 1 LIMIT 1;

