UPDATE `sgulot` SET `mcudot` = '\r\n<p><strong>כי ה\'&#160;יתן</strong> לך <strong>חכמה מפיו</strong> - יגלה לך מסתרי החכמה, כאילו מפיו יאמר לך.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 6 AND 1 LIMIT 1;

