UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>מי שהוא <strong>חרוץ</strong> (זריז) במעשיו להרויח במעשה <strong>ידיו</strong>, סופו תרום קרנו <strong>וימשול</strong>, אם-כן ידו משלה לו; &#160; אבל יד המרויח ב<strong>רמיה</strong>, <strong>תהיה להמסה</strong> וירד מטה מטה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 24 AND 1 LIMIT 1;

