UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>האדם <strong>לא</strong> יהיה <strong>נכון</strong> במעשה ה<strong>רשע</strong> - לא יעמוד על כנו ובסיסו עם ההון שאסף ברשע;&#160;&#160; אבל <strong>שורש צדיקים</strong> לא יהיה נוח לפול - אף אם יפול, <strong>לא ימוט</strong> עם השורש, כי יחזור ויקום.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 3 AND 1 LIMIT 1;

