UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>אין שום עבירה <strong>מאונה</strong> (מזדמנת) <strong>לצדיק</strong> להיות נכשל בה;&#160;&#160; אבל <strong>הרשעים נתמלאו מרע</strong>, כי עבירה גוררת עבירה עד אין תכלית.</p>', `tirgum` = '<p><strong>לצדיק</strong>  לא תקרה \r\n<strong>תאונה</strong>  שבה הוא יעשה \r\n<strong>אוון</strong>  (רע) בטעות;</p><p>אולם ה<strong>רשעים</strong> יעשו מעשים רעים עד שיהיו <strong>מלאים</strong> עד גדותיהם ב<strong>רע</strong>, הרע \"יגלוש\" מהם ויתגלה ברבים והם ייתפסו וייענשו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 21 AND 1 LIMIT 1;

