UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>נפשו</strong> של <strong>עצל</strong> היא <strong>תתאוה</strong> לכל <strong>ואין</strong> לה, כי לרוב עצלותו לא ישיג דבר;&#160;&#160;&#160; אבל <strong>נפש</strong> ה<strong>חרוצים</strong> (זריזים) <strong>תדושן</strong> (תשמן), כי ישיגו תאוותם ברוב הזריזות.\r\n\r\n\r\n\r\n</p>', `tirgum` = '<p><strong>העצל מתאווה</strong> ורוצה דברים רבים <strong>ואינו</strong> משיג אותם, כי לרצות זה לא מספיק - צריך גם לעשות;&#160;&#160; אולם&#160;<strong>החרוץ מדושן</strong> ומשיג שפע רב.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 4 AND 1 LIMIT 1;

