UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>בז לדבר</strong> - <strong>המבזה</strong> מה ממצוות ה\' הוא <strong>חובל</strong> בעצמו; אבל <strong>הירא</strong> מן <strong>המצוה</strong> ומגדל מעלתה, <strong>ישולם</strong> לו שכר גם בעבור זה.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יחבל</strong> - מלשון חבלה ומכה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 13 AND 1 LIMIT 1;

