UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>המייחל</strong> לדבר מה, והיא <strong>נמשכת</strong> ומתאחרת לבוא, היא לו ל<strong>מחלה </strong>וכאב <strong>לב</strong>;&#160;&#160; וכאשר <strong>באה התאוה</strong>, היא לו ל<strong>עץ</strong> המגדל <strong>חיים</strong>, וירופא מכאב הלב.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 12 AND 1 LIMIT 1;

