UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>ריש</strong> - עניות ובזיון מעותדים לבוא על המבטל <strong>המוסר</strong>; אבל <strong>השומר</strong> בלבו דברי <strong>התוכחות</strong>, <strong>יכובד</strong> מן הבריות.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>פורע</strong> - ענין ביטול.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 18 AND 1 LIMIT 1;

