UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<br />', `main_qod_subjects` = NULL, `hqblot` = '<p>בכל הפרק שלנו, האישה היא החזקה - היא מצליחה להטות ולהדיח את הנער כרצונה - הוא משתוקק אליה והיא מושלת בו. זאת בניגוד ל<a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t0103.htm#16\">בראשית ג16</a>: \"<q class=\"psuq\">אֶל הָאִשָּׁה אָמַר... וְאֶל אִישֵׁךְ תְּשׁוּקָתֵךְ <strong>וְהוּא יִמְשָׁל</strong> בָּךְ</q>\" (ע\"פ <a href=\"http://tora.us.fm/tnk1/tora/brejit/br-03-16.html\">חגי הופר</a>).\r\n		</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 7 AND `verse_number` = 21 AND 1 LIMIT 1;

