UPDATE `sgulot` SET `prefix` = NULL, `hqblot` = '<p>ספר משלי לא רק מתאר את ה<strong>מחלה</strong> אלא גם מציע תרופות:</p><p>- <a class=\"psuq\" href=\"/tnk1/prqim/t2810.htm#28\">משלי י28</a>: \"<q class=\"psuq\"><strong>תּוֹחֶלֶת</strong> צַדִּיקִים שִׂמְחָה</q>\" (<a href=\"/tnk1/ktuv/mj/10-28.html\">פירוט</a>);</p><p>- <a class=\"psuq\" href=\"/tnk1/prqim/t2818.htm#14\">משלי יח14</a>: \"<q class=\"psuq\">רוּחַ אִישׁ יְכַלְכֵּל<strong> מַחֲלֵהוּ</strong></q>\" (<a href=\"/tnk1/ktuv/mj/18-14.html\">פירוט</a>);</p><p>ראו פירושינו שם.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 12 AND 1 LIMIT 1;

