UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>כאשר <strong>נהיה</strong> (נשברה) <strong>תאות</strong> האדם בענייני הגוף, אז <strong>תערב</strong> (תמתק) <strong>לנפש</strong> המשכלת, כי אז תשכיל;&#160;&#160; אבל הרשעים, דבר <strong>תעוב</strong> הוא להם כאשר המה <strong>סרים מן הרע</strong> מחסרון התאוה, כי יותר יחפצו שיתאוו לעבירה ויעשוה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 19 AND 1 LIMIT 1;

