UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>הרבה מאכל</strong> בא לעולם ע\"י <strong>ניר</strong> (חרישת) <strong>העניים</strong>, החורשים בשדה תבואה, והנה העשירים אשר לא עמלו בו יאכלום;&#160;&#160; וכמו-כן יש מי אשר <strong>נספה</strong> (נאסף ומת) מכל העולם <strong>בלא משפט</strong> מוות, כי לא עשה דבר אשר יומת בעבורו ונתפס הוא בעוון אחר.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 23 AND 1 LIMIT 1;

