UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>לשובע</strong> - אינו חפץ במעדנים ורק בדבר <strong>המשביע</strong> את <strong>הנפש</strong>; אבל <strong>הרשעים</strong> יתענגו באכילת מעדנים מתוקים וערבים, ותמיד <strong>בטנו חסרה</strong>, כי רווחא לבסימא שכיחא ויוכל עוד למלאותה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 25 AND 1 LIMIT 1;

