UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הצדיק</strong> אינו חפץ במעדנים ורק <strong>אוכל</strong> דבר <strong>המשביע</strong> את <strong>הנפש</strong>;&#160;&#160; אבל <strong>הרשעים</strong> יתענגו באכילת מעדנים מתוקים וערבים, ותמיד <strong>בטנו חסרה</strong>, כי \"רווחא לבסימא שכיחא\"<small class=\"small\"> (למאכל טעים תמיד יש מקום בבטן)</small> ויוכל עוד למלאותה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>- ה<strong>צדיק אוכל</strong> את הכמות המדוייקת הדרושה כדי \r\n<strong>להשביע</strong>   את <strong>נפשו</strong>, לא פחות ולא יותר;&#160;&#160;&#160; אבל \r\n<strong>הרשעים </strong>זוללים בלי הגבלה עד \r\nש<strong>בטנם</strong>   מתפקעת ותמיד <strong>חסר</strong>   להם מקום ב<strong>בטן</strong> כדי להכיל את כל האוכל שהם מנסים לדחוס לתוכה.</p>', `main_qod_subjects` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 25 AND 1 LIMIT 1;

