UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>כל אחת מה<strong>חכמות</strong> שב<strong>נשים בנתה ביתה</strong>, ר\"ל: מעמדת ביתה על בסיסה; &#160;&#160; <strong>ואיוולת</strong> <strong>מהרסת</strong> ביתה <strong>בידיה</strong>.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 1 AND 1 LIMIT 1;

