UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק   <strong class=\"selflink\">ג</strong>                                </p>\r\n</div>\r\n\r\n<p><strong>חוטר גאוה</strong> - גאוה זקופה כמקל.</p>\r\n<p><strong>תשמורם</strong> - מלדבר גאות.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>חוטר</strong> - מטה, כמו (): <strong>ויצא חוטר מגזע ישי</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 3 AND 1 LIMIT 1;

