UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>עד</strong> אשר יתמיד לדבר <strong>אמונות</strong>, בודאי <strong>לא יכזב</strong> בדבר העדות;&#160;&#160; אבל הרגיל <strong>להפיח</strong> (לדבר) <strong>כזבים</strong>, ואם המה בדברים של מה בכך, מכל-מקום, הואיל ורגיל בכך <strong>יעיד</strong> גם-כן <strong>שקר</strong>, ויש לחקור הרבה בדבריו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 5 AND 1 LIMIT 1;

