UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אדם \r\n<strong>ערום</strong>  ופיקח <strong>מבין</strong> מראש את סופה של כל <strong>דרך</strong>-פעולה אפשרית, ומבין שהכי משתלם לנהוג ביושר;&#160;&#160; לעומתו, אנשים <strong>כסילים </strong>פועלים ב<strong>איוולת</strong> (שטחיות), אינם רואים את הנולד ולכן חושבים שיצליחו להרויח בדרכי-<strong>מרמה</strong>.</p>' WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 8 AND 1 LIMIT 1;

