UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק          <strong class=\"selflink\">י</strong>                         </p>\r\n</div>\r\n\r\n<p><strong>לב יודע</strong> - <strong>לבו</strong> של האדם הוא <strong>יודע</strong> ומרגיש ב<strong>מרירות נפשו</strong> בעמל התורה, ואין זולתו מרגיש בצערו של זה, לכך <strong>בשמחתו</strong> בעת קבול השכר <strong>לא יתערב זר</strong> לחלוק בו, כי לבדו יקבל שכרו משלם.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יתערב</strong> - מלשון תערובות.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 10 AND 1 LIMIT 1;

