UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>גם</strong> (אפילו) <strong>ב</strong>עבור רבוי ה<strong>שחוק</strong>, עם כי לא עבר בזה על מצות ה\', מכל-מקום <strong>יכאב לבו</strong> בעת יקבל הגמול על שפנה לבו לבטלה;&#160;&#160;&#160;<strong>ו</strong>אם-כן, <strong>אחריתה</strong> (סופה) תהיה אשר ה<strong>שמחה</strong> תהפך ל<strong>תוגה</strong> (יגון ועצבון).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 13 AND 1 LIMIT 1;

