UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>קצר אפים</strong>, שאינו מאריך אפו וממהר לכעוס, היא לו ל<strong>איוולת</strong>;&#160;&#160;&#160; <strong>ואיש מזמות</strong>, הנמשך אחר מחשבות לבו לבל יפלס דבר הטובה היא אם רעה, יהיה <strong>שנוא</strong> לכל - לשמים ולבריות.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 17 AND 1 LIMIT 1;

