UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>עד אמת</strong> הוא <strong>מציל נפשות</strong>, כי אם יעיד להרוג הרוצח אז יציל מידו נפשות בני אדם, ואם יעיד לפטרו אז יציל נפש הנדון;&#160;&#160;&#160; <strong>ויפיח</strong> (המדבר) <strong>כזבים</strong> מציל בכזביו את איש <strong>מרמה</strong>, כי מסייע לו בעדותו.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 25 AND 1 LIMIT 1;

