UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>יראת ה\'</strong> הוא <strong>מקור</strong> הנובע <strong>חיים</strong>, כי היראה תזהירו <strong>לסור ממוקשי מות</strong>, הם עוונות ופשעים.\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p>', `tirgum` = '<p><strong>יראת ה\'</strong> היא <strong>מקור</strong> הנותן <strong>חיים</strong> - היא מייעצת לאדם מה לעשות כדי לשפר את איכות חייו;&#160;&#160;&#160; יראת ה\' עוזרת  גם <strong>לסור ממוקשים</strong> (מכשולים) המביאים<strong> מוות</strong> - היא מייעצת לאדם מה <strong>לא</strong> לעשות כדי להינצל ממוות.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 27 AND 1 LIMIT 1;

