UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק                                   <strong class=\"selflink\">לה</strong></p>\r\n</div>\r\n\r\n<p><strong>רצון מלך</strong> - בנקל <strong>יתרצה המלך לעבד משכיל</strong>, כי אף אם יכעוס עליו יתרצה לו בעבור רוב שכלו.</p>\r\n<p>אבל על <strong>עבד</strong> הנמצא בו כל דבר <strong>בושה</strong>, לפי רוב הסכלות, <strong>עברתו</strong> עליו שמורה נצח.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>ועברתו</strong> - מלשון עברה וכעס.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 35 AND 1 LIMIT 1;

