UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>חכמים</strong> - אם מצאו <strong>עושר</strong>, <strong>מעטר</strong> אותם, כי יוכר לַכֹּל תפארתם ונדיבות-ליבם בתתם לאביונים די המחסור ובפנים יפות; &#160;&#160; </p><p><strong>וכסילים</strong> - במעשה <strong>איוולתם</strong> תוכר <strong>האיוולת</strong> הטמונה בליבם, כי בעבור היות כל מעשיהם איוולת, נראה מזה כי יש עוד הרבה איוולת תקועה בליבם, ויגולה בעת ההזדמן.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n' WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 24 AND 1 LIMIT 1;

