UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>ארך אפים</strong> - מי שאינו ממהר לכעוס - הוא <strong>רב תבונה</strong>,&#160;&#160; אבל <strong>קצר רוח</strong> הממהר לכעוס הוא <strong>מרים האולת</strong> על התבונה, כי על-ידי הכעס החכמה מסתלקת, ובוחר הוא אם-כן באיוולת יותר מן החכמה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 29 AND 1 LIMIT 1;

