UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>תועבת ה\'</strong> - כשעדיין לא שבו מרשעם, ולא התפללו על המחילה.</p>\r\n<p><strong>ותפלת ישרים</strong> - השואלים מחילה בעת הבאת הזבח, התפלה ההיא היא לרצון לה\'.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 8 AND 1 LIMIT 1;

