UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>דרך רשע</strong> - ההולך לעשות רשע, ועם שלא עשאה.</p>\r\n<p><strong>ומרדף</strong> - ה<strong>רודף</strong> לעשות <strong>צדקה</strong>, אף אם נאנס ולא עשאה, מכל-מקום <strong>יאהב</strong> לה\'.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 9 AND 1 LIMIT 1;

