UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>ה<strong>חכם</strong> <strong>ירא</strong> (יפחד) מדבר המפחיד ונשמר ממנו, ולזה הוא <strong>סר מן הרעה</strong> ולא תבוא עליו; &#160;&#160; אבל ה<strong>כסיל</strong> <strong>מת</strong>מלא <strong>עברה</strong> להתקצף על כל, <strong>ובוטח</strong> בעצמו שלא יאונה לו רעה, ולא כן הוא; וקיצר בדבר המובן מעצמו.</p>' WHERE `book` = 'משל' AND `chapter_number` = 14 AND `verse_number` = 16 AND 1 LIMIT 1;

