UPDATE `sgulot` SET `mcudot` = '<p>הדברים האלה נאמרים על שני דרכים: <strong>להבין את המשל</strong> - מה שהמשיל אל המליצה, וגם את <strong>המליצה</strong> עצמה: כשאומר בהמליצה להשמר מזונה, ואמורה להמשיל על המינות, הנה יש להבין בה להיות נזהר ממינות, גם מאשה זונה;&#160;&#160;&#160; (ולתוספת ביאור אמר):&#160;&#160; <strong>דברי חכמים </strong>האמורים בפה מלא, והרמוזים בדרך <strong>חידה</strong> (דברים סתומים קשה ההבנה).</p>', `tirgum` = '<p>- כי יש בלימוד רבדים רבים: הקורא יכול <strong>להבין</strong>  את המשמעות המילולית הפשוטה של כל <strong>משל</strong>, אך אם יתעמק יותר יבין גם את ה<strong>מליצה</strong> (הנמשל);&#160;&#160; הוא יוכל למצוא כאן&#160;<strong>דברי חכמים</strong> פשוטים, אך גם <strong>חידות</strong> מתוחכמות.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 6 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `mcudot` = '<p>(עתה החל לדבר דבריו, ואמר:)&#160;&#160;&#160; <strong>בראשית</strong> למוד <strong>הדעת</strong> יקנה <strong>יראת ה\'</strong>, כי הלא <strong>אוילים </strong>(רשעים) <strong>בזו את החכמה והמוסר</strong>, ואם לא יקנה יראת ה\' בראשיתה - מה תועלת בלימודה? הלא יבזה אותה וימאס בה.</p>', `tirgum` = '<p><strong>יראת</strong>-כבוד כלפי<strong> ה\'</strong> היא הצעד <strong>הראשון</strong> ב<strong>ידיעת חכמה ומוסר</strong>!&#160;&#160; ה<strong>בזים</strong> לה\' ומנסים ללמוד חכמה בלי יראת ה\', לא יצליחו להבין את עומק החכמה ויישארו <strong>אוילים</strong> (שטחיים).</p><p>/ <strong>יראת ה\'</strong> (ראיית נוכחות ה\' המאחדת את כל המציאות) היא גם ה<strong>ראשית</strong> (העיקר) בלימוד \r\n  <strong>דעת חכמה ומוסר</strong>;&#160;&#160; אולם ה<strong>אוילים</strong> (השטחיים) לומדים כל דבר-חכמה בנפרד ואינם רואים את האחדות ולכן <strong>בזים</strong> לה\'.</p>' WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 7 AND 1 LIMIT 1;

