UPDATE `sgulot` SET `tirgum` = '<p>- כי <strong>ה\' מתעב</strong> (שונא ומרחיק מעליו) את <strong>הנלוז</strong> (המזלזל בדרכי היושר) המחקה את הרשעים;&#160;&#160;&#160; <strong>ו</strong>ה\' אוהב את <strong>הישרים</strong> ומגלה להם את <strong>סודותיו</strong>, סודות המדע והטבע, וכך מאפשר להם לגבור על אנשי החמס בלי לרדת לרמתם. </p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 32 AND 1 LIMIT 1;

