UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- והעיזים  (הנקבות) יוצרות, מאותם עשבים חסרי-ערך שאספו, \r\n<strong>חלב עיזים</strong>  בכמות שתספיק לטבל בו את \r\n<strong>לחמך</strong>  ואת לחם בני \r\n<strong>ביתך</strong>, ולתת \r\n<strong>חיים</strong>  ובריאות \r\n<strong>לנערות</strong>  המשרתות בביתך.</p>', `tosfot` = NULL, `kotrt` = 'חלב עיזים טוב לבריאות' WHERE `book` = 'משל' AND `chapter_number` = 27 AND `verse_number` = 27 AND 1 LIMIT 1;

