UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n המתמיד \r\n<strong>לעבד</strong>   ולטפח את \r\n<strong>אדמתו</strong>    (האדמה הפרטית שלו), \r\n<strong>ישבע לחם</strong>     גם כשלא יוכל למצוא עבודה בשדות של אחרים;&#160;&#160; אך מי שמזניח את האדמה הפרטית שלו \r\n<strong>ורודף</strong>    אחרי עסקים \r\n<strong>ריקים</strong>   ולא יצרניים - בסוף יישאר בלי כלום \r\n<strong>ו\'ישבע\'</strong>    רק \r\n<strong>ריש</strong>    (עוני).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 28 AND `verse_number` = 19 AND 1 LIMIT 1;

