UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>ה<strong>בוטח</strong>  (סומך) רק על מחשבות \r\n<strong>ליבו</strong>  ואינו מתייעץ עם אחרים \r\n<strong>הוא כסיל</strong>,&#160;&#160; אולם \r\n<strong>ההולך בחכמה</strong>  ויודע גם ללמוד מאחרים \r\n<strong>הוא יימלט</strong>  מצרות.</p>', `hqblot` = '<p>החובה שלא ללכת בעיניים עצומות אחרי הלב נזכרה גם ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t0415.htm#39\">במדבר טו39</a>: \"<q class=\"psuq\">וְהָיָה\r\n לָכֶם לְצִיצִת וּרְאִיתֶם אֹתוֹ, וּזְכַרְתֶּם אֶת כָּל מִצְוֹת ה\' \r\nוַעֲשִׂיתֶם אֹתָם, \r\n<strong>וְלֹא תָּתוּרוּ אַחֲרֵי לְבַבְכֶם</strong> וְאַחֲרֵי עֵינֵיכֶם \r\nאֲשֶׁר אַתֶּם זֹנִים אַחֲרֵיהֶם</q>\".</p>  \r\n<p>\r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2803.htm#5\">משלי ג5</a>: \"<q class=\"psuq\"><strong>בְּטַח</strong> אֶל ה\' \r\n<strong>בְּכָל לִבֶּךָ</strong>, וְאֶל בִּינָתְךָ אַל תִּשָּׁעֵן</q>\" (<a href=\"http://localhost/tnk1/ktuv/mjly/mj-03-05.html\">פירוט</a>)\r\n</p> \r\n<p>\r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2814.htm#16\">משלי יד16</a>: \"<q class=\"psuq\"><strong>חָכָם</strong> יָרֵא וְסָר מֵרָע, \r\n<strong>וּכְסִיל</strong> מִתְעַבֵּר \r\n<strong>וּבוֹטֵחַ</strong></q>\" (<a href=\"http://localhost/tnk1/ktuv/mjly/mj-14-16.html\">פירוט</a>)</p> \r\n<p>על בטחון עצמי חיובי בעולם החי ובמלוכה, ראו \r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2830.htm#29\">משלי ל29</a>: \"<q class=\"psuq\">שְׁלֹשָׁה הֵמָּה מֵיטִיבֵי צָעַד וְאַרְבָּעָה מֵיטִבֵי לָכֶת</q>\" (<a href=\"http://localhost/tnk1/ktuv/mjly/mj-30-2931.html\">פירוט</a>).</p> \r\n<p>\r\n<a href=\"http://localhost/tnk1/ktuv/mjly/xkma_bina_wguf.html\">חכמה באזניים - בינה בלב</a>\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 28 AND `verse_number` = 26 AND 1 LIMIT 1;

