UPDATE `sgulot` SET `hqblot` = '<p>יש לשפוט כל אדם בצדק - גם את האנשים הפשוטים וגם את ה<strong>נדיבים</strong> (הגדולים הנכבדים): </p><p>\r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t0319.htm#15\">ויקרא יט15</a>: \"<q class=\"psuq\">לֹא תַעֲשׂוּ עָוֶל בַּמִּשְׁפָּט: לֹא תִשָּׂא פְּנֵי דָל,\r\nוְלֹא תֶהְדַּר פְנֵי גָדוֹל;\r\nבְּצֶדֶק תִּשְׁפֹּט עֲמִיתֶךָ</q>\".\r\n  \r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 26 AND 1 LIMIT 1;

