UPDATE `sgulot` SET `mcudot` = '<p><strong>לא יחפוץ כסיל בתבונה</strong> - להוסיף בינה על מה שבידו, <strong>כי אם בהתגלות ליבו</strong> - להשמיע לרבים מעט חכמה שבידו להתפאר בה.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 18 AND `verse_number` = 2 AND 1 LIMIT 1;

