UPDATE `sgulot` SET `mcudot` = '<p><strong>פועל</strong> המאסף <strong>אוצרות</strong> ע\"י <strong>לשון שקר</strong>, המה <strong>הבל נידף</strong>, כי לא יתקיימו בידו, ו<strong>יבקשו</strong> עוד את ה<strong>מוות</strong>, כי המה סיבה למות על ידם.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 6 AND 1 LIMIT 1;

