UPDATE `sgulot` SET `mcudot` = '<p><strong>שוד רשעים יגורם</strong> - הגזל עצמו יביא עליהם פחד,&#160; <strong>כי מאנו לעשות משפט</strong> - להשיב הגזל לבעליו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 7 AND 1 LIMIT 1;

