UPDATE `sgulot` SET `mcudot` = '\r\n<p>אין מעשה ה<strong>רשע</strong> על כי יטעה לחשוב שמעשיו טובים, אבל יודע הוא ברעתם <strong>ונפשו איוותה</strong> (חשקה) להם; &#160; ולזה <strong>לא יוחן</strong> (ימצא חן) <strong>בעיניו רעהו</strong> הרשע כמותו, כי אם חשבה לטובה, אם כן מהראוי לאהוב לרעהו כמשפט אנשי הצדק.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 10 AND 1 LIMIT 1;

