UPDATE `sgulot` SET `mcudot` = '<p>הנותן <strong>מתן בסתר</strong> למי שחרה אפו בו, הנה המתן הזה&#160; <strong>יכפה</strong> (יכסה) את ה<strong>אף</strong> ולא יהיה נראה עוד;&#160;&#160; והנותן <strong>שוחד בחיק</strong>, ר\"ל בהסתר, אז יכפה בזה <strong>חמה עזה</strong> וחזקה; וכפל הדבר במילים שונות.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 14 AND 1 LIMIT 1;

