UPDATE `sgulot` SET `mcudot` = '<p><strong>אדם תועה מדרך השכל</strong> (מדרך התורה), רק אחר המיתה, כשיהיה <strong>בקהל</strong> ה<strong>רפאים</strong> (המתים), אז <strong>ינוח</strong> ולא יתעה עוד; אבל כל ימי חייו הוא מוסיף והולך לתעות בכל יום יותר.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 16 AND 1 LIMIT 1;

