UPDATE `sgulot` SET `mcudot` = '<p><strong>הצדיק ישמח </strong>בעת <strong>יעשה משפט</strong>, <strong>ו</strong>עשיית ה<strong>מחיתה</strong> (שבר) היא שמחה <strong>לפועלי אוון</strong>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 15 AND 1 LIMIT 1;

