UPDATE `sgulot` SET `mcudot` = '<p><strong>זבח רשעים</strong>, אשר עודם ברשעתם בעת יזבחו הקרבן כי לא שבו לה\', הנה הקרבן הוא <strong>תועבה</strong>;&#160;&#160; <strong>אף כי</strong> (כל-שכן) שתועבה היא כאשר <strong>יביאנו</strong> <strong>בזמה</strong> (במחשבה רעה), לרמות את הבריות, להחשיבו לצדיק וזובח לאלהים.</p>', `mqorot` = '<h3>אף כי בזימה יביאנו</h3><p>        המושג \r\n<strong>זימה</strong>      כולל סוגים שונים של מחשבות ומעשים רעים:\r\n</p><p>1. \"<q class=\"mfrj\">להרשיעו ובעצה רעה, כגון קרבנות בלעם ובלק שלא היו מביאים אלא לקלל את ישראל</q>\" \r\n<small>(רש\"י)</small>.\r\n</p>       \r\n<p>2. \"<q class=\"mfrj\">במחשבה רעה לרמות את הבריות להחשיבו לצדיק וזובח לאלהים</q>\" \r\n<small>(מצודת דוד)</small>.\r\n</p>       \r\n<p>3. \"<q class=\"mfrj\">השתקעות המחשבה בעומק הרהורי עבירה... גם בעת הבאת הקרבן\r\nכוונתו לזימה ועצת חטא, שחוץ ממה שלא יתקבל מפני המביא, לא יתקבל מפני\r\nההבאה בעצמה, שתכליתה לרע</q>\" \r\n<small>(מלבי\"ם)</small>.</p>       \r\n<p>4. \"<q class=\"mfrj\">בעבירה, שהוא גזול או משאר עבירה... בעת שמתפלל הוא חושב מחשבות זרות ופניות, להרים גאוותו ולהתנשא</q>\" \r\n<small>(הגאון מווילנה, וכן רמ\"ּד ואלי)</small>.</p>' WHERE `book` = 'משל' AND `chapter_number` = 21 AND `verse_number` = 27 AND 1 LIMIT 1;

