UPDATE `sgulot` SET `mcudot` = '<p><strong>אם</strong> לא יהיה <strong>לך לשלם</strong>, <strong>למה</strong> תגרום שהמלוה <strong>יקח</strong> ממך מצעות <strong>משכבך</strong>?</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 22 AND `verse_number` = 27 AND 1 LIMIT 1;

