UPDATE `sgulot` SET `mcudot` = '\r\n<p><strong>כי&#160;כמו המשער</strong> בעצמו דבר מה שאין בזה שום ממש, כי-אם דמיון ומחשבה בעלמא, <strong>כן</strong> המאכיל הזה, אף כי <strong>יאמר לך אכול ושתה</strong>, הנה <strong>לבו בל עמך</strong>, ולא יחפוץ שתאכל באמת, ואין ממש בדבריו.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 7 AND 1 LIMIT 1;

